import React, { Component } from 'react';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    // root: {
    //   display: 'flex',

    //     margin: theme.spacing(0),

    //     boxShadow: "7",

    // },


    rootTextRight: {
      // display:'flex',
      width: '100%',
      maxWidth: 500,
      float: 'right',
      fontSize: '120px',
      marginRight: '60px',

    },
    rootTextLeft: {
      // display:'flex',
      width: '100%',
      maxWidth: 500,
      float: 'left',
      fontSize: '120px',
      margin: '60px',

    },


  }));


function NewsItemsLeft (props){

        const classes = useStyles();
        return (
            <div align="left" className={classes.rootTextLeft}>
                <Typography variant="h1" gutterBottom>
                    {props.title}
                </Typography>
                <Typography variant="h3" gutterBottom>
                    {props.writter}
                </Typography>
                <Typography variant="h5" >
                    {props.content}
                </Typography>
            </div>
        )


}
function NewsItemsRight (props){

        const classes = useStyles();
        return (
            <div align="right" className={classes.rootTextRight}>
                <Typography variant="h1" gutterBottom>
                    {props.title}
                </Typography>
                <Typography variant="h3" gutterBottom>
                    {props.writter}
                </Typography>
                <Typography variant="h5" >
                    {props.content}
                </Typography>
            </div>
        )


}


