import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import { shadows } from '@material-ui/system';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    '& > *': {
      margin: theme.spacing(1),

      boxShadow: "0",
      float: "left"
    },
  },
  rootText: {
    width: '100%',
    maxWidth: 500,
    float: 'right',
    fontSize:'120px'
  },

  // small: {
  //   width: theme.spacing(3),
  //   height: theme.spacing(3),
  // },
  large: {
    width: theme.spacing(70),
    height: theme.spacing(70),
  },
}));


function Text() {

  const classes = useStyles();
  return (
    <div align="left" className={classes.rootText}>


      <Typography variant="h2" gutterBottom>
      ‹‹እግዚአብሔርን መፍራት›› (መዝ. ፴፫፥፲፩)
      </Typography>
      <Typography variant="h5" gutterBottom>
      ዲያቆን ዮሴፍ በቀለ
      </Typography>
      <Typography variant="h5" gutterBottom>
      ነቢዩ ዳዊት ‹‹ልጆቼ ሆይ ኑ፥ ስሙኝ፤ እግዚአብሔርን መፍራት አስተምራቹሁ ዘንድ›› ያለን ሰው አምላኩ እግዚአብሔርን በመፍራት ሊኖር እንደሚገባ ሲገልጽ ነው፡፡ በዚሁ መዝሙር ላይ  ‹‹ሕይወትን የሚፈቅድ ሰው ማን ነው? በጎ ዘመንንም ለማየት የሚወድድ ማን ነው?? አንደበትህን ከክፉ ከልክል፥ ከንፈሮችህም ሽንገላን እንዳይናገሩ። ከክፉ ሽሽ፥ መልካምንም አድርግ፤ ሰላምን ሻት፥ ተከተላትም። የእግዚአብሔር ዐይኖች ወደ ጻድቃኑ፥ ጆሮዎቹም ወደ ልመናቸው ናቸውና›› ብሏል፤ ይህም እኛ ከፈጣሪያችን ጋር እንኖር ዘንድ ማድረግ የሚገባንን ሁሉ እንድንፈጽም ነው። (መዝ. ፴፫፥፲፩-፲፭)

በዚህም የሰው ልጅ ሲፈጠር ጀምሮ የእግዚአብሔርን ሕግ በማክበርና ትእዛዛቱን በመፈጸም ዘወትር እግዚአብሔርን በመፍራት እና በመታዘዝ እንዲኖር ተፈጥሯል። ምክንያቱም እኛም ጠቢቡ ሰለሞን በምሳሌ ፩፥፯ ላይ እና ቅዱስ ዳዊት በመዝሙር ፻፲፥፲ ላይ ሁለቱም የጥበብ መጀመሪያ አምላካችን እግዚአብሔርን መፍራት እንደሆነ ነግረውናል።
      </Typography>
      <Typography variant="button" display="block" gutterBottom>
        button text
      </Typography>
      <Typography variant="caption" display="block" gutterBottom>
        caption text
      </Typography>
      <Typography variant="overline" display="block" gutterBottom>
        overline text
      </Typography>
    </div>
  )

}

export default function Services() {
  const classes = useStyles();


  return (
    <div id="services" className="text-center">
      <div className="container">
        <div className="section-title">
          <h2>Our Services</h2>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit duis sed
            dapibus leonec.
            </p>
        </div>
        <div className="row container" >
        <Text />
          <Avatar alt="Remy Sharp" src="img/portfolio/01-small.jpg" className={classes.large} />

        </div>
        {/* <Grid
            container
            spacing={6}
            direction="row"

            alignItems="center"
          >
            <NewsReviewCard />

            <NewsReviewCard />
            <NewsReviewCard />
            <NewsReviewCard />
            <NewsReviewCard />
            <NewsReviewCard />
            <NewsReviewCard />
            <NewsReviewCard />
            <NewsReviewCard />

          </Grid> */}


        {/* <div className="row">
            {this.props.data
              ? this.props.data.map((d, i) => (
                  <div  key={`${d.name}-${i}`} className="col-md-4">
                    {" "}
                    <i className={d.icon}></i>
                    <div className="service-desc">
                      <h3>{d.name}</h3>
                      <p>{d.text}</p>
                    </div>
                  </div>
                ))
              : "loading"}
          </div> */}
      </div>
    </div>
  );

}
